function MCP_requireSetting_(key, notes) {
  const v = MCP_getSetting_(key);
  if (v) return v;

  MCP_ensureSettingKeyExists_(key, notes || "");
  SpreadsheetApp.getUi().alert(
    "SETTINGS manquant",
