    if (typeof IAPF_appendMemoryEntry_ === "function") {
      IAPF_appendMemoryEntry_({
        type: "CONSTAT",
        title: "MCP ‚Äî D√©ploiement automatis√© (non impl√©ment√©)",
        details: "N√©cessite Cloud Run Job + settings GCP",
        source: "MCP_COCKPIT",
        tags: "MCP;DEPLOY"
      });
    }

  } catch (e) {
    ui.alert("MCP ‚Äî D√©ploiement", "Erreur : " + String(e && e.message ? e.message : e), ui.ButtonSet.OK);
  }
}

/**
 * G09_API_ENDPOINT.gs
 * WebApp entrypoint: doPost/doGet -> secure dispatcher
 */

function doPost(e) {
  return IAPF_API.handleRequest_(e);
}

function doGet(e) {
  // Healthcheck minimal, no secrets, no internal details.
  const out = {
    ok: true,
    service: "IAPF_MCP_WEBAPP",
    mode: IAPF_API.getMode_(),
    time: new Date().toISOString()
  };
  return ContentService
    .createTextOutput(JSON.stringify(out))
    .setMimeType(ContentService.MimeType.JSON);
}

var IAPF_API = (function () {

  function handleRequest_(e) {
    const nowIso = new Date().toISOString();
    let requestId = "unknown";
    try {
      const rawBody = (e && e.postData && e.postData.contents) ? e.postData.contents : "";
      const headers = (e && e.parameter) ? e.parameter : {}; // Note: WebApp doesn't pass headers directly
      // We'll read token via query param fallback ONLY if needed (discouraged).
      // Recommended: token in body for WebApp limitation unless using external proxy.
      const parsed = rawBody ? JSON.parse(rawBody) : {};

      requestId = (parsed && parsed.request_id) ? String(parsed.request_id) : "unknown";
      const token = extractToken_(e, parsed);

      // AUTH
      const auth = IAPF_AUTH.authorizeRequest(token, parsed);
      if (!auth.ok) {
        safeLog_(nowIso, "CONSTAT", "API_AUTH_REFUSED", {
          request_id: requestId,
          reason: auth.reason
        }, "API");
        return jsonOut_(403, {
          ok: false,
          mode: getMode_(),
          request_id: requestId,
          dry_run: true,
          errors: [{ code: "AUTH_REFUSED", message: auth.reason }]
        });
      }

      // Attach role to request for downstream gating (dispatcher).
      // Note: keep internal field name underscore-prefixed to avoid collisions.
      parsed._auth = { role: String(auth.role || "OPERATOR") };

      // VALIDATE ENVELOPE
      const v = validateEnvelope_(parsed);
      if (!v.ok) {
        safeLog_(nowIso, "CONSTAT", "API_INVALID_REQUEST", {
          request_id: requestId,
          errors: v.errors
        }, "API");
        return jsonOut_(400, {
          ok: false,
          mode: getMode_(),
          request_id: requestId,
          dry_run: true,
          errors: v.errors
        });
      }

      // DISPATCH
      const dispatch = IAPF_DISPATCH.dispatch(parsed);

      // LOG (always)
      safeLog_(nowIso,
        dispatch.ok ? (dispatch.dry_run ? "CONSTAT" : "DECISION") : "CONSTAT",
        dispatch.log_title,
        dispatch.log_details,
        "API"
      );

      return jsonOut_(200, dispatch.response);

    } catch (err) {
      safeLog_(nowIso, "CONSTAT", "API_CRASH", {
        request_id: requestId,
        message: String(err && err.message ? err.message : err)
      }, "API");

      return jsonOut_(500, {
        ok: false,
        mode: getMode_(),
        request_id: requestId,
        dry_run: true,
        errors: [{ code: "SERVER_ERROR", message: "Internal error" }]
      });
    }
  }

  function extractToken_(e, parsed) {
    // WebApp header access is limited; safest pragmatic approach:
    // token must be in body: parsed.auth.token
    // (If you later add a proxy, you can pass headers to body.)
    if (parsed && parsed.auth && parsed.auth.token) return String(parsed.auth.token);

    // Optional fallback: ?token=... for emergency manual tests (not recommended)
    if (e && e.parameter && e.parameter.token) return String(e.parameter.token);

    return "";
  }

  function validateEnvelope_(req) {
    const errors = [];
    if (!req || typeof req !== "object") {
      return { ok: false, errors: [{ code: "INVALID_JSON", message: "Body must be JSON object" }] };
    }
    if (!req.action || typeof req.action !== "string") errors.push({ code: "MISSING_ACTION", message: "action is required" });
    if (!req.request_id || typeof req.request_id !== "string") errors.push({ code: "MISSING_REQUEST_ID", message: "request_id is required" });
    if (!req.ts || typeof req.ts !== "string") errors.push({ code: "MISSING_TS", message: "ts is required" });

    // payload can be absent; will default to {}
    if (req.payload && typeof req.payload !== "object") errors.push({ code: "INVALID_PAYLOAD", message: "payload must be object" });

    // confirm is optional boolean
    if (req.confirm !== undefined && typeof req.confirm !== "boolean") {
      errors.push({ code: "INVALID_CONFIRM", message: "confirm must be boolean" });
    }

    // confirm_token required only when confirm=true
    if (req.confirm === true && (!req.confirm_token || typeof req.confirm_token !== "string")) {
      errors.push({ code: "MISSING_CONFIRM_TOKEN", message: "confirm_token required when confirm=true" });
    }

    return { ok: errors.length === 0, errors: errors };
  }

  function jsonOut_(statusCode, obj) {
    // Apps Script ContentService doesn't allow setting HTTP status.
    // We'll embed status in response for client handling.
    const payload = Object.assign({ http_status: statusCode }, obj);
    return ContentService
      .createTextOutput(JSON.stringify(payload))
      .setMimeType(ContentService.MimeType.JSON);
  }

  function safeLog_(tsIso, type, title, detailsObj, source) {
    // Always log via MEMORY_LOG writer (existing function)
    try {
      const details = JSON.stringify(detailsObj || {});
      IAPF_appendMemoryEntry(tsIso, type, title, details, Session.getActiveUser().getEmail() || "system", source || "API", "IAPF;MCP;API");
    } catch (e) {
      // last resort: avoid throwing
      try { Logger.log("LOG_FAIL " + String(e)); } catch (_) {}
    }
  }

  function getMode_() {
    return IAPF_AUTH.getApiMode_();
  }

  return {
    handleRequest_: handleRequest_,
    getMode_: getMode_
  };

})();

/**
 * G10_AUTH.gs
 * Token auth + anti-replay confirm_token mint/verify (usage unique).
 */

var IAPF_AUTH = (function () {

  function getApiMode_() {
    const props = PropertiesService.getScriptProperties();
    return String(props.getProperty("IAPF_API_MODE") || "SAFE");
  }

  function getToken_() {
    const props = PropertiesService.getScriptProperties();
    return String(props.getProperty("IAPF_API_TOKEN") || "");
  }

  function getAuditorToken_() {
    // Optional secondary token dedicated to read-only auditors (e.g. √âlia).
    // If not set, only IAPF_API_TOKEN is accepted.
    const props = PropertiesService.getScriptProperties();
    return String(props.getProperty("IAPF_API_TOKEN_AUDITOR") || "");
  }

  function getSalt_() {
    const props = PropertiesService.getScriptProperties();
    return String(props.getProperty("IAPF_API_SALT") || "");
  }

  function authorizeRequest(token, req) {
    const expected = getToken_();
    if (!expected || expected.length < 24) {
      return { ok: false, reason: "API token not configured" };
    }

    var role = "";
    if (token && token === expected) {
      role = "OPERATOR";
    } else {
      const auditor = getAuditorToken_();
      if (auditor && auditor.length >= 24 && token === auditor) {
        role = "AUDITOR";
      } else {
        return { ok: false, reason: "Invalid token" };
      }
    }

    // minimal time sanity (avoid accidental replay with stale payload)
    // SAFE mode: reject if ts older than 10 minutes
    try {
      const mode = getApiMode_();
      if (mode === "SAFE") {
        const ts = new Date(String(req.ts || ""));
        const now = new Date();
        const ageMs = Math.abs(now.getTime() - ts.getTime());
        if (isNaN(ts.getTime())) return { ok: false, reason: "Invalid ts" };
        if (ageMs > 10 * 60 * 1000) return { ok: false, reason: "Stale request" };
      }
    } catch (e) {
      return { ok: false, reason: "Invalid ts" };
    }

    return { ok: true, role: role };
  }

  function mintConfirmToken(planObj) {
    // confirm token: short-lived nonce, usage unique
    // store in Script Cache or Properties (cache is better, but not always reliable across quotas)
    const nonce = Utilities.getUuid();
    const salt = getSalt_();
    const payload = JSON.stringify(planObj || {});
    const sig = Utilities.base64Encode(Utilities.computeHmacSha256Signature(payload + "|" + nonce, salt));
    const token = nonce + "." + sig;

    // store nonce as "unused" for 5 minutes
    CacheService.getScriptCache().put("IAPF_CONFIRM_" + nonce, "1", 300);

    return token;
  }

  function verifyAndConsumeConfirmToken(token, planObj) {
    if (!token || typeof token !== "string" || token.indexOf(".") === -1) return { ok: false, reason: "Invalid confirm token" };
    const parts = token.split(".");
    if (parts.length !== 2) return { ok: false, reason: "Invalid confirm token" };

    const nonce = parts[0];
    const sig = parts[1];

    // must exist and be unused
    const cache = CacheService.getScriptCache();
    const state = cache.get("IAPF_CONFIRM_" + nonce);
    if (!state) return { ok: false, reason: "Confirm token expired or already used" };

    const salt = getSalt_();
    const payload = JSON.stringify(planObj || {});
    const expectedSig = Utilities.base64Encode(Utilities.computeHmacSha256Signature(payload + "|" + nonce, salt));

    if (sig !== expectedSig) return { ok: false, reason: "Confirm token mismatch" };

    // consume
    cache.remove("IAPF_CONFIRM_" + nonce);

    return { ok: true };
  }

  return {
    getApiMode_: getApiMode_,
    authorizeRequest: authorizeRequest,
    mintConfirmToken: mintConfirmToken,
    verifyAndConsumeConfirmToken: verifyAndConsumeConfirmToken
  };

})();

/**
 * G11_SHEET_ALIAS.gs
 * Centralized sheet alias resolver (fix RISKS/RISQUES without patching every file).
 */

var IAPF_SHEETS = (function () {

  // Canonical keys used by code
  var MAP = {
    "MEMORY_LOG": ["MEMORY_LOG"],
    "SNAPSHOT_ACTIVE": ["SNAPSHOT_ACTIVE"],
    "CONFLITS_DETECTES": ["CONFLITS_DETECTES"],
    "DEPENDANCES_SCRIPTS": ["DEPENDANCES_SCRIPTS"],
    "CARTOGRAPHIE_APPELS": ["CARTOGRAPHIE_APPELS"],
    "REGLES_DE_GOUVERNANCE": ["REGLES_DE_GOUVERNANCE"],
    "RISKS": ["RISKS", "RISQUES"] // <‚Äî your issue fixed here
  };

  function resolveName_(key) {
    const ss = SpreadsheetApp.getActiveSpreadsheet();
    const names = MAP[String(key)] || [String(key)];
    for (var i = 0; i < names.length; i++) {
      const sh = ss.getSheetByName(names[i]);
      if (sh) return names[i];
    }
    return null;
  }

  function getSheet_(key) {
    const ss = SpreadsheetApp.getActiveSpreadsheet();
    const name = resolveName_(key);
    if (!name) throw new Error("Sheet not found for key: " + key);
    return ss.getSheetByName(name);
  }

  function getName_(key) {
    const name = resolveName_(key);
    if (!name) throw new Error("Sheet not found for key: " + key);
    return name;
  }

  return {
    getSheet_: getSheet_,
    getName_: getName_
  };

})();

/**
 * G12_API_DISPATCH.gs
 * Allowlist dispatcher with dry-run default and confirm_token execution gate.
 * PATCH: add READ-only actions for GPT Desktop (no confirm needed).
 */

var IAPF_DISPATCH = (function () {

  var ALLOWLIST = {
    "INITIALIZE_DAY": true,
    "CLOSE_DAY": true,
    "GENERATE_SNAPSHOT": true,
    "APPEND_MEMORY_ENTRY": true,
    "GLOBAL_AUDIT": true,
    "EXPORT_HUB": true,

    // READ-only
    "READ_MEMORY_LOG": true,
    "READ_SNAPSHOT_ACTIVE": true,
    "READ_HUB_STATUS": true,
    "LIST_ACTIONS": true,

    // READ-only connectors (inspection)
    "RO_DRIVE_TREE": true,
    "RO_DRIVE_FILE_META": true,
    "RO_DRIVE_CHANGES": true,

    "RO_SCRIPT_PROJECT_DETAIL": true,
    "RO_SCRIPT_PROJECT_CONTENT": true,
    "RO_SCRIPT_DEPLOYMENTS": true,
    "RO_SCRIPT_VERSIONS": true,

    "RO_GITHUB_REPOS": true,
    "RO_GITHUB_REPO_DETAIL": true,
    "RO_GITHUB_WORKFLOW_RUNS": true
  };

  var READ_ONLY = {
    "READ_MEMORY_LOG": true,
    "READ_SNAPSHOT_ACTIVE": true,
    "READ_HUB_STATUS": true,
    "LIST_ACTIONS": true,

    // READ-only connectors (inspection)
    "RO_DRIVE_TREE": true,
    "RO_DRIVE_FILE_META": true,
    "RO_DRIVE_CHANGES": true,

    "RO_SCRIPT_PROJECT_DETAIL": true,
    "RO_SCRIPT_PROJECT_CONTENT": true,
    "RO_SCRIPT_DEPLOYMENTS": true,
    "RO_SCRIPT_VERSIONS": true,

    "RO_GITHUB_REPOS": true,
    "RO_GITHUB_REPO_DETAIL": true,
    "RO_GITHUB_WORKFLOW_RUNS": true
  };

  function dispatch(req) {
    const action = String(req.action || "");
    const payload = req.payload || {};
    const confirm = (req.confirm === true);
    const requestId = String(req.request_id || "unknown");
    const mode = IAPF_AUTH.getApiMode_();
    const role = (req && req._auth && req._auth.role) ? String(req._auth.role) : "OPERATOR";

    if (!ALLOWLIST[action]) {
      return build_(false, true, requestId, "API_ACTION_REFUSED", {
        request_id: requestId,
        action: action,
        reason: "Action not allowed"
      }, {
        ok: false,
        mode: mode,
        request_id: requestId,
        dry_run: true,
        errors: [{ code: "ACTION_NOT_ALLOWED", message: "Action not allowed" }]
      });
    }

    // READ-only: execute immediately (auth already done at API layer), still logged.
    if (READ_ONLY[action]) {
      const out = execute_(action, payload, { auth: (req && req._auth) ? req._auth : {} });
      return build_(true, false, requestId, "API_READ_OK", {
        request_id: requestId,
        action: action,
        role: role
      }, {
        ok: true,
        mode: mode,
        request_id: requestId,
        dry_run: false,
        result: out
      });
    }

    // WRITE actions are restricted to OPERATOR role
    if (role !== "OPERATOR") {
      return build_(false, true, requestId, "API_ROLE_REFUSED", {
        request_id: requestId,
        action: action,
        role: role,
        reason: "Role not allowed for write actions"
      }, {
        ok: false,
        mode: mode,
        request_id: requestId,
        dry_run: true,
        errors: [{ code: "ROLE_NOT_ALLOWED", message: "Role not allowed for write actions" }]
      });
    }

    // Build a plan (dry-run) for every WRITE action
    const plan = planFor_(action, payload);

    if (!confirm) {
      const confirmToken = IAPF_AUTH.mintConfirmToken(plan);
      return build_(true, true, requestId, "API_DRY_RUN_PLAN", {
        request_id: requestId,
        action: action,
        plan: plan
      }, {
        ok: true,
        mode: mode,
        request_id: requestId,
        dry_run: true,
        confirm_token: confirmToken,
        result: { plan: plan }
      });
    }

    // confirm=true requires confirm_token and verification
    const ver = IAPF_AUTH.verifyAndConsumeConfirmToken(String(req.confirm_token || ""), plan);
    if (!ver.ok) {
      return build_(false, true, requestId, "API_CONFIRM_REFUSED", {
        request_id: requestId,
        action: action,
        reason: ver.reason
      }, {
        ok: false,
        mode: mode,
        request_id: requestId,
        dry_run: true,
        errors: [{ code: "CONFIRM_REFUSED", message: ver.reason }]
      });
    }

    // Execute now (controlled)
    const exec = execute_(action, payload, { auth: (req && req._auth) ? req._auth : {} });

    return build_(true, false, requestId, "API_EXECUTED", {
      request_id: requestId,
      action: action,
      execution: exec
    }, {
      ok: true,
      mode: mode,
      request_id: requestId,
      dry_run: false,
      result: exec
    });
  }

  function planFor_(action, payload) {
    switch (action) {
      case "INITIALIZE_DAY":
        return { action: action, will_call: "MCP_IMPL_initializeDay", side_effects: ["writes: HUB day state", "writes: MEMORY_LOG"] };
      case "CLOSE_DAY":
        return { action: action, will_call: "MCP_IMPL_closeDay", side_effects: ["writes: HUB day state", "writes: MEMORY_LOG"] };
      case "GENERATE_SNAPSHOT":
        return { action: action, will_call: "IAPF_generateSnapshot", side_effects: ["writes: Drive snapshot", "writes: SNAPSHOT_ACTIVE"] };
      case "APPEND_MEMORY_ENTRY":
        return { action: action, will_call: "IAPF_appendMemoryEntry_", side_effects: ["writes: MEMORY_LOG"], input: sanitizeMemoryPayload_(payload) };
      case "GLOBAL_AUDIT":
        return { action: action, will_call: "MCP_IMPL_globalAudit", side_effects: ["reads: all hub tabs", "writes: audit output tabs"] };
      case "EXPORT_HUB":
        return { action: action, will_call: "MCP_EXPORT_exportHubZipAndSheet", side_effects: ["writes: Drive export ZIP+XLSX"] };
      default:
        return { action: action, will_call: "UNKNOWN" };
    }
  }

  function execute_(action, payload, ctx) {
    ctx = ctx || {};
    switch (action) {

      // WRITE actions
      case "INITIALIZE_DAY":
        return { action: action, output: MCP_IMPL_initializeDay() };
      case "CLOSE_DAY":
        return { action: action, output: MCP_IMPL_closeDay() };
      case "GENERATE_SNAPSHOT":
        return { action: action, output: IAPF_generateSnapshot() };
      case "APPEND_MEMORY_ENTRY": {
        var p = sanitizeMemoryPayload_(payload);
        // Use the canonical writer in your HUB (supports positional & object)
        if (typeof IAPF_appendMemoryEntry_ === "function") {
          IAPF_appendMemoryEntry_(p.type, p.title, p.details, { source: p.source, tags: p.tags, author: p.author, ts_iso: p.ts_iso });
        } else if (typeof IAPF_appendMemoryEntry === "function") {
          IAPF_appendMemoryEntry(p.ts_iso, p.type, p.title, p.details, p.author, p.source, p.tags);
        } else {
          throw new Error("No memory writer found (IAPF_appendMemoryEntry_ / IAPF_appendMemoryEntry)");
        }
        return { action: action, output: "MEMORY_LOG_APPENDED" };
      }
      case "GLOBAL_AUDIT":
        return { action: action, output: MCP_IMPL_globalAudit() };
      case "EXPORT_HUB":
        return { action: action, output: MCP_EXPORT_exportHubZipAndSheet() };

      // READ-only actions
      case "LIST_ACTIONS":
        return {
          allowlist: Object.keys(ALLOWLIST).sort(),
          read_only: Object.keys(READ_ONLY).sort(),
          roles: ["OPERATOR", "AUDITOR"]
        };

      case "READ_MEMORY_LOG":
        return readMemoryLog_(payload);

      case "READ_SNAPSHOT_ACTIVE":
        return readSnapshotActive_(payload);

      case "READ_HUB_STATUS":
        return readHubStatus_();

      // CONNECTORS ‚Äî Drive
      case "RO_DRIVE_TREE":
        return (typeof IAPF_RO_driveTree_ === "function") ? IAPF_RO_driveTree_(payload) : { error: "Missing IAPF_RO_driveTree_" };
      case "RO_DRIVE_FILE_META":
        return (typeof IAPF_RO_driveFileMeta_ === "function") ? IAPF_RO_driveFileMeta_(payload, (ctx && ctx.auth) ? ctx.auth : {}) : { error: "Missing IAPF_RO_driveFileMeta_" };
      case "RO_DRIVE_CHANGES":
        return (typeof IAPF_RO_driveChanges_ === "function") ? IAPF_RO_driveChanges_(payload) : { error: "Missing IAPF_RO_driveChanges_" };

      // CONNECTORS ‚Äî Apps Script
      case "RO_SCRIPT_PROJECT_DETAIL":
        return (typeof IAPF_RO_scriptProjectDetail_ === "function") ? IAPF_RO_scriptProjectDetail_(payload) : { error: "Missing IAPF_RO_scriptProjectDetail_" };
      case "RO_SCRIPT_PROJECT_CONTENT":
        return (typeof IAPF_RO_scriptProjectContent_ === "function") ? IAPF_RO_scriptProjectContent_(payload) : { error: "Missing IAPF_RO_scriptProjectContent_" };
      case "RO_SCRIPT_DEPLOYMENTS":
        return (typeof IAPF_RO_scriptDeployments_ === "function") ? IAPF_RO_scriptDeployments_(payload) : { error: "Missing IAPF_RO_scriptDeployments_" };
      case "RO_SCRIPT_VERSIONS":
        return (typeof IAPF_RO_scriptVersions_ === "function") ? IAPF_RO_scriptVersions_(payload) : { error: "Missing IAPF_RO_scriptVersions_" };

      // CONNECTORS ‚Äî GitHub
      case "RO_GITHUB_REPOS":
        return (typeof IAPF_RO_githubRepos_ === "function") ? IAPF_RO_githubRepos_(payload) : { error: "Missing IAPF_RO_githubRepos_" };
      case "RO_GITHUB_REPO_DETAIL":
        return (typeof IAPF_RO_githubRepoDetail_ === "function") ? IAPF_RO_githubRepoDetail_(payload) : { error: "Missing IAPF_RO_githubRepoDetail_" };
      case "RO_GITHUB_WORKFLOW_RUNS":
        return (typeof IAPF_RO_githubWorkflowRuns_ === "function") ? IAPF_RO_githubWorkflowRuns_(payload) : { error: "Missing IAPF_RO_githubWorkflowRuns_" };

      default:
        throw new Error("Action not executable: " + action);
    }
  }

  function readMemoryLog_(payload) {
    var p = payload || {};
    // Backward compatible paging:
    // - legacy: { limit, offset }
    // - new:    { page_size, cursor }
    var limit = clamp_(Number(p.page_size || p.limit || 50), 1, 200);
    var offset = Math.max(0, Number(p.offset || 0));
    if (p.cursor) {
      var decoded = decodeCursorOffset_(String(p.cursor || ""));
      if (!isNaN(decoded)) offset = Math.max(0, decoded);
    }
    var reverse = (p.reverse !== false); // default true

    var sh = getSheetByKey_("MEMORY_LOG");
    var lastRow = sh.getLastRow();
    if (lastRow < 2) {
      return { rows: [], count: 0, limit: limit, offset: offset, reverse: reverse };
    }

    // Read all rows then slice (safe enough; capped by limit+offset, but we keep simple & stable)
    var data = sh.getRange(2, 1, lastRow - 1, 7).getValues();
    if (reverse) data.reverse();

    var sliced = data.slice(offset, offset + limit);

    var rows = sliced.map(function (r) {
      return {
        ts_iso: String(r[0] || ""),
        type: String(r[1] || ""),
        title: String(r[2] || ""),
        details: String(r[3] || ""),
        author: String(r[4] || ""),
        source: String(r[5] || ""),
        tags: String(r[6] || "")
      };
    });

    var nextOffset = offset + rows.length;
    var nextCursor = (nextOffset < data.length) ? encodeCursorOffset_(nextOffset) : "";

    return {
      rows: rows,
      count: data.length,
      limit: limit,
      page_size: limit,
      offset: offset,
      reverse: reverse,
      next_cursor: nextCursor
    };
  }

  function encodeCursorOffset_(offset) {
    try {
      var json = JSON.stringify({ o: Number(offset || 0) });
      return Utilities.base64EncodeWebSafe(json);
    } catch (e) {
      return "";
    }
  }

  function decodeCursorOffset_(cursor) {
    try {
      var json = Utilities.newBlob(Utilities.base64DecodeWebSafe(cursor)).getDataAsString();
      var obj = JSON.parse(json || "{}");
      return Number(obj.o || 0);
    } catch (e) {
      return NaN;
    }
  }

  function readSnapshotActive_(payload) {
    var sh = getSheetByKey_("SNAPSHOT_ACTIVE");
    var lastRow = sh.getLastRow();
    var lastCol = sh.getLastColumn();
    if (lastRow < 1 || lastCol < 1) return { values: [] };

    var maxRows = clamp_(Number((payload || {}).max_rows || 20), 1, 200);
    var maxCols = clamp_(Number((payload || {}).max_cols || 10), 1, 50);

    var r = Math.min(lastRow, maxRows);
    var c = Math.min(lastCol, maxCols);

    var values = sh.getRange(1, 1, r, c).getValues();
    return { values: values, rows: r, cols: c };
  }

  function readHubStatus_() {
    var keys = ["MEMORY_LOG", "SNAPSHOT_ACTIVE", "DEPENDANCES_SCRIPTS", "CARTOGRAPHIE_APPELS", "REGLES_DE_GOUVERNANCE", "RISKS", "CONFLITS_DETECTES"];
    var out = {};
    keys.forEach(function (k) {
      try {
        var sh = getSheetByKey_(k);
        out[k] = { ok: true, name: sh.getName() };
      } catch (e) {
        out[k] = { ok: false, error: String(e && e.message ? e.message : e) };
      }
    });
    return out;
  }

  function getSheetByKey_(key) {
    if (typeof IAPF_SHEETS !== "undefined" && IAPF_SHEETS && typeof IAPF_SHEETS.getSheet_ === "function") {
      return IAPF_SHEETS.getSheet_(key);
    }
    // fallback: direct name
    var ss = SpreadsheetApp.getActiveSpreadsheet();
    var sh = ss.getSheetByName(String(key));
    if (!sh) throw new Error("Sheet not found: " + key);
    return sh;
  }

  function clamp_(n, min, max) {
    if (isNaN(n)) return min;
    return Math.max(min, Math.min(max, n));
  }

  function sanitizeMemoryPayload_(payload) {
    var p = payload || {};
    return {
      ts_iso: String(p.ts_iso || new Date().toISOString()),
      type: String(p.type || "CONSTAT"),
      title: String(p.title || "API_ENTRY"),
      details: String(p.details || "{}"),
      author: String(p.author || (Session.getActiveUser().getEmail() || "system")),
      source: String(p.source || "API"),
      tags: String(p.tags || "IAPF;MCP;API")
    };
  }

  function build_(ok, dryRun, requestId, logTitle, logDetails, response) {
    return {
      ok: ok,
      dry_run: dryRun,
      log_title: logTitle,
      log_details: logDetails,
      response: response
    };
  }

  return {
    dispatch: dispatch
  };

})();

/**
 * G13_READONLY_CONNECTORS.gs
 *
 * READ-ONLY Connectors for inspection (Apps Script / Drive / GitHub).
 * Goal: enable MCP to audit environments without external console.
 *
 * Governance:
 * - No destructive operation.
 * - Pagination via cursor tokens.
 * - Minimal payloads by default (avoid giant responses).
 * - Secrets NEVER returned.
 */

/** =========================
 *  Helpers
 *  ========================= */

function IAPF_RO_getSetting_(key) {
  try {
    if (typeof IAPF_getConfig_ === "function") return String(IAPF_getConfig_(String(key)) || "");
  } catch (e) {}

  // Fallback: SETTINGS sheet (col A=key, col B=value)
  try {
    var ss = SpreadsheetApp.getActiveSpreadsheet();
    var sh = ss.getSheetByName("SETTINGS");
    if (!sh) return "";
    var last = sh.getLastRow();
    if (last < 2) return "";
    var data = sh.getRange(2, 1, last - 1, 2).getValues();
    for (var i = 0; i < data.length; i++) {
      if (String(data[i][0] || "").trim() === String(key).trim()) {
        return String(data[i][1] || "").trim();
      }
    }
  } catch (e2) {}

  return "";
}

function IAPF_RO_clamp_(n, min, max) {
  n = Number(n);
  if (isNaN(n)) n = min;
  return Math.max(min, Math.min(max, n));
}

function IAPF_RO_encodeCursor_(obj) {
  try {
    return Utilities.base64EncodeWebSafe(JSON.stringify(obj || {}));
  } catch (e) {
    return "";
  }
}

function IAPF_RO_decodeCursor_(cursor) {
  try {
    if (!cursor) return null;
    var json = Utilities.newBlob(Utilities.base64DecodeWebSafe(String(cursor))).getDataAsString();
    return JSON.parse(json || "{}");
  } catch (e) {
    return null;
  }
}

function IAPF_RO_sha256_(text) {
  try {
    var bytes = Utilities.computeDigest(Utilities.DigestAlgorithm.SHA_256, String(text || ""), Utilities.Charset.UTF_8);
    return bytes.map(function (b) {
      var v = (b < 0) ? b + 256 : b;
      var h = v.toString(16);
      return h.length === 1 ? "0" + h : h;
    }).join("");
  } catch (e) {
    return "";
  }
}

/** =========================
 *  Drive
 *  ========================= */

function IAPF_RO_driveListChildren_(parentId, pageToken, pageSize) {
  var args = {
    q: "'" + String(parentId) + "' in parents and trashed = false",
    pageSize: IAPF_RO_clamp_(pageSize || 100, 1, 200),
    fields: "nextPageToken,files(id,name,mimeType,parents,modifiedTime,createdTime,size,md5Checksum,webViewLink,trashed)"
  };
  if (pageToken) args.pageToken = String(pageToken);

  // Advanced Drive service (Drive API v3) is enabled in appsscript.json
  return Drive.Files.list(args);
}

function IAPF_RO_driveTree_(payload) {
  var p = payload || {};

  // Defaults grounded in existing SETTINGS
  var rootId = String(p.root_id || IAPF_RO_getSetting_("memory_root_folder_id") || "");
  if (!rootId) {
    return { ok: false, error: "Missing root_id and SETTINGS.memory_root_folder_id" };
  }

  var maxDepth = IAPF_RO_clamp_(p.max_depth || 2, 0, 10);
  var maxItems = IAPF_RO_clamp_(p.max_items || 200, 1, 500);
  var pageSize = IAPF_RO_clamp_(p.page_size || 100, 1, 200);

  // Cursor state
  // { q: [{id,depth}], idx, pageToken }
  var state = IAPF_RO_decodeCursor_(p.cursor) || { q: [{ id: rootId, depth: 0 }], idx: 0, pageToken: "" };
  if (!state.q || !state.q.length) state = { q: [{ id: rootId, depth: 0 }], idx: 0, pageToken: "" };

  var out = [];
  var safety = 0;

  while (out.length < maxItems && state.idx < state.q.length && safety < 1000) {
    safety++;

    var node = state.q[state.idx];
    var parentId = String(node.id);
    var depth = Number(node.depth || 0);

    // Always list children for current node
    var resp = IAPF_RO_driveListChildren_(parentId, state.pageToken || "", pageSize);
    var files = (resp && resp.files) ? resp.files : [];

    files.forEach(function (f) {
      var mime = String(f.mimeType || "");
      var isFolder = (mime === "application/vnd.google-apps.folder");

      out.push({
        id: String(f.id || ""),
        name: String(f.name || ""),
        mimeType: mime,
        isFolder: isFolder,
        parentId: parentId,
        depth: depth + 1,
        modifiedTime: String(f.modifiedTime || ""),
        createdTime: String(f.createdTime || ""),
        size: (f.size !== undefined && f.size !== null) ? String(f.size) : "",
        md5Checksum: String(f.md5Checksum || ""),
        webViewLink: String(f.webViewLink || ""),
        trashed: !!f.trashed
      });

      // Expand queue (folders only) within maxDepth
      if (isFolder && (depth + 1) <= maxDepth) {
        state.q.push({ id: String(f.id), depth: depth + 1 });
      }
    });

    // Pagination handling for current folder
    if (resp && resp.nextPageToken) {
      state.pageToken = String(resp.nextPageToken);
    } else {
      state.idx = Number(state.idx || 0) + 1;
      state.pageToken = "";
    }

    if (out.length >= maxItems) break;
  }

  var nextCursor = (state.idx < state.q.length) ? IAPF_RO_encodeCursor_(state) : "";

  return {
    ok: true,
    root_id: rootId,
    max_depth: maxDepth,
    returned: out.length,
    items: out,
    next_cursor: nextCursor
  };
}

function IAPF_RO_driveFileMeta_(payload, auth) {
  var p = payload || {};
  var role = (auth && auth.role) ? String(auth.role) : "OPERATOR";

  var fileId = String(p.file_id || "");
  if (!fileId) return { ok: false, error: "Missing file_id" };

  var fields = "id,name,mimeType,parents,owners,createdTime,modifiedTime,size,md5Checksum,webViewLink,iconLink,trashed";
  var meta = Drive.Files.get(fileId, { fields: fields });

  var out = {
    id: String(meta.id || ""),
    name: String(meta.name || ""),
    mimeType: String(meta.mimeType || ""),
    parents: meta.parents || [],
    createdTime: String(meta.createdTime || ""),
    modifiedTime: String(meta.modifiedTime || ""),
    size: (meta.size !== undefined && meta.size !== null) ? String(meta.size) : "",
    md5Checksum: String(meta.md5Checksum || ""),
    webViewLink: String(meta.webViewLink || ""),
    iconLink: String(meta.iconLink || ""),
    trashed: !!meta.trashed
  };

  // Owners: redact email addresses by default (AUDITOR always redacted)
  var owners = meta.owners || [];
  out.owners = owners.map(function (o) {
    return {
      displayName: String(o.displayName || ""),
      permissionId: String(o.permissionId || ""),
      // emailAddress only for OPERATOR if explicitly asked
      emailAddress: (role === "OPERATOR" && p.include_emails === true) ? String(o.emailAddress || "") : ""
    };
  });

  // Optional permissions list (redacted by default)
  if (p.include_permissions === true) {
    try {
      var permResp = Drive.Permissions.list(fileId, {
        fields: "permissions(id,type,role,domain,allowFileDiscovery,deleted,displayName,emailAddress)"
      });
      var perms = (permResp && permResp.permissions) ? permResp.permissions : [];

      out.permissions = perms.map(function (x) {
        return {
          id: String(x.id || ""),
          type: String(x.type || ""),
          role: String(x.role || ""),
          domain: String(x.domain || ""),
          allowFileDiscovery: !!x.allowFileDiscovery,
          deleted: !!x.deleted,
          displayName: String(x.displayName || ""),
          emailAddress: (role === "OPERATOR" && p.include_emails === true) ? String(x.emailAddress || "") : ""
        };
      });
    } catch (e) {
      out.permissions_error = String(e && e.message ? e.message : e);
    }
  }

  return { ok: true, file: out };
}

function IAPF_RO_driveChanges_(payload) {
  var p = payload || {};
  var maxItems = IAPF_RO_clamp_(p.max_items || 100, 1, 200);

  // Cursor state: { pageToken, startPageToken }
  var state = IAPF_RO_decodeCursor_(p.cursor) || {};

  var startPageToken = String(p.start_page_token || state.startPageToken || "");
  var pageToken = String(p.page_token || state.pageToken || "");

  if (!pageToken) {
    if (!startPageToken) {
      // Create a new startPageToken
      var spt = Drive.Changes.getStartPageToken();
      startPageToken = String(spt.startPageToken || "");
    }
    pageToken = startPageToken;
  }

  var resp = Drive.Changes.list(pageToken, {
    pageSize: maxItems,
    fields: "newStartPageToken,nextPageToken,changes(fileId,time,removed,file(id,name,mimeType,parents,modifiedTime,trashed))"
  });

  var changes = (resp && resp.changes) ? resp.changes : [];
  var items = changes.map(function (c) {
    var f = c.file || {};
    return {
      fileId: String(c.fileId || ""),
      time: String(c.time || ""),
      removed: !!c.removed,
      file: f ? {
        id: String(f.id || ""),
        name: String(f.name || ""),
        mimeType: String(f.mimeType || ""),
        parents: f.parents || [],
        modifiedTime: String(f.modifiedTime || ""),
        trashed: !!f.trashed
      } : null
    };
  });

  var next = "";
  if (resp && resp.nextPageToken) {
    next = IAPF_RO_encodeCursor_({
      startPageToken: startPageToken,
      pageToken: String(resp.nextPageToken)
    });
  }

  return {
    ok: true,
    start_page_token: startPageToken,
    new_start_page_token: String(resp && resp.newStartPageToken ? resp.newStartPageToken : ""),
    returned: items.length,
    changes: items,
    next_cursor: next
  };
}

/** =========================
 *  Apps Script (script.googleapis.com)
 *  ========================= */

function IAPF_RO_scriptApiGet_(path, query) {
  var base = "https://script.googleapis.com/v1";
  var url = base + String(path || "");

  var q = query || {};
  var parts = [];
  Object.keys(q).forEach(function (k) {
    if (q[k] === undefined || q[k] === null || q[k] === "") return;
    parts.push(encodeURIComponent(k) + "=" + encodeURIComponent(String(q[k])));
  });
  if (parts.length) url += (url.indexOf("?") === -1 ? "?" : "&") + parts.join("&");

  var token = ScriptApp.getOAuthToken();
  var resp = UrlFetchApp.fetch(url, {
    method: "get",
    muteHttpExceptions: true,
    headers: {
      Authorization: "Bearer " + token
    }
  });

  var status = resp.getResponseCode();
  var text = resp.getContentText() || "";
  if (status < 200 || status >= 300) {
    return { ok: false, http: status, error: text.substring(0, 2000) };
  }

  try {
    return { ok: true, data: JSON.parse(text || "{}") };
  } catch (e) {
    return { ok: true, data: { raw: text } };
  }
}

function IAPF_RO_resolveScriptId_(payload) {
  var p = payload || {};
  var target = String(p.target || "HUB").toUpperCase();

  if (p.script_id) return String(p.script_id);

  if (target === "HUB") {
    return ScriptApp.getScriptId();
  }

  if (target === "BOX2026") {
    var id = IAPF_RO_getSetting_("box2026_script_id");
    return String(id || "");
  }

  return "";
}

function IAPF_RO_scriptProjectDetail_(payload) {
  var scriptId = IAPF_RO_resolveScriptId_(payload);
  if (!scriptId) return { ok: false, error: "Missing script_id and cannot resolve target" };

  var res = IAPF_RO_scriptApiGet_("/projects/" + encodeURIComponent(scriptId), {});
  if (!res.ok) return res;

  // Return only stable fields
  var d = res.data || {};
  return {
    ok: true,
    script_id: scriptId,
    title: String(d.title || ""),
    parentId: String(d.parentId || ""),
    updateTime: String(d.updateTime || ""),
    createTime: String(d.createTime || "")
  };
}

function IAPF_RO_scriptProjectContent_(payload) {
  var p = payload || {};
  var scriptId = IAPF_RO_resolveScriptId_(p);
  if (!scriptId) return { ok: false, error: "Missing script_id and cannot resolve target" };
  var maxFiles = IAPF_RO_clamp_(p.max_files || 200, 1, 500);

  var res = IAPF_RO_scriptApiGet_("/projects/" + encodeURIComponent(scriptId) + "/content", {});
  if (!res.ok) return res;

  var files = (res.data && res.data.files) ? res.data.files : [];
  files = files.slice(0, maxFiles);

  var outFiles = files.map(function (f) {
    var name = String(f.name || "");
    var type = String(f.type || "");
    var source = (f.source !== undefined && f.source !== null) ? String(f.source) : "";
    var hash = IAPF_RO_sha256_(source);

    return {
      name: name,
      type: type,
      sha256: hash
    };
  });

  return {
    ok: true,
    script_id: scriptId,
    returned: outFiles.length,
    files: outFiles
  };
}

function IAPF_RO_scriptDeployments_(payload) {
  var p = payload || {};
  var scriptId = IAPF_RO_resolveScriptId_(p);
  if (!scriptId) return { ok: false, error: "Missing script_id and cannot resolve target" };

  var pageSize = IAPF_RO_clamp_(p.page_size || 50, 1, 100);
  var cursor = IAPF_RO_decodeCursor_(p.cursor) || {};
  var pageToken = String(p.page_token || cursor.pageToken || "");

  var res = IAPF_RO_scriptApiGet_("/projects/" + encodeURIComponent(scriptId) + "/deployments", {
    pageSize: pageSize,
    pageToken: pageToken
  });
  if (!res.ok) return res;

  var d = res.data || {};
  var deployments = (d.deployments || []).map(function (x) {
    return {
      deploymentId: String(x.deploymentId || ""),
      updateTime: String(x.updateTime || ""),
      // manifestFileName is stable and helps debug without leaking config
      manifestFileName: (x.deploymentConfig && x.deploymentConfig.manifestFileName) ? String(x.deploymentConfig.manifestFileName) : "",
      versionNumber: (x.deploymentConfig && x.deploymentConfig.versionNumber !== undefined) ? String(x.deploymentConfig.versionNumber) : ""
    };
  });

  var next = "";
  if (d.nextPageToken) next = IAPF_RO_encodeCursor_({ pageToken: String(d.nextPageToken) });

  return {
    ok: true,
    script_id: scriptId,
    returned: deployments.length,
    deployments: deployments,
    next_cursor: next
  };
}

function IAPF_RO_scriptVersions_(payload) {
  var p = payload || {};
  var scriptId = IAPF_RO_resolveScriptId_(p);
  if (!scriptId) return { ok: false, error: "Missing script_id and cannot resolve target" };

  var pageSize = IAPF_RO_clamp_(p.page_size || 50, 1, 100);
